div.script:
function concatenate()
{
    var last_name = document.getElementById("last_name").value;
    var middle_name = document.getElementById("middle_name").value;
    var first_name = document.getElementById("first_name").value;
    var name = last_name + ", " + first_name + " " + middle_name;
    document.getElementById("name").value = name;
}

document.getElementById("last_name").onchange = concatenate;
document.getElementById("middle_name").onchange = concatenate;
document.getElementById("first_name").onchange = concatenate;

function readUrl(input){
    if (input.files && input.files[0]){

        var reader = new FileReader();

        reader.onload = function(e){
            $('#applicant_img').attr('src', e.target.result);
        };
        reader.readAsDataURL(input.files[0]);
    }
}
