body.script:
function readUrl(input){
    if (input.files && input.files[0]){

        var reader = new FileReader();

        reader.onload = function(e){
            $('#applicant_img').attr('src', e.target.result);
        };
        reader.readAsDataURL(input.files[0]);
    }
}

//SAMPLE SAMPLE
$(document).on('click', 'a.js_add_family_details', function (ev) {
    ev.preventDefault();
    var pathname = this.pathname;
    var image_path;
    image_path ='';
    if($('#applicant_img')[0].src.search("base64") > 0){

        image_path = $('#applicant_img')[0].src.split("base64")[1].substr(1,$('#applicant_img')[0].src.split("base64")[1].length);

    }

    openerp.jsonRpc("/jobs/updateApplication", 'call', {
            'new_applicant_id': $('input[name="new_applicant_id"]')[0].value,
            'first_name': $('input[name="first_name"]')[0].value,
            'job_applied_id': $('select[name="job_id"]')[0].value,
            'nationality_id': $('select[name="country_id"]')[0].value,
            'last_name': $('input[name="last_name"]')[0].value,
            'maiden_name': $('input[name="maiden_name"]')[0].value,
            'date_of_birth': $('input[name="date_of_birth"]')[0].value,
            'place_of_birth': $('input[name="place_of_birth"]')[0].value,
            'height': $('input[name="height"]')[0].value,
            'weight': $('input[name="weight"]')[0].value,
            'preferred_nickname': $('input[name="preferred_nickname"]')[0].value,
            'shoe_size': $('input[name="shoe_size"]')[0].value,
            'email_address': $('input[name="email_from"]')[0].value,
            'contact_number': $('input[name="phone"]')[0].value,
            'sss_no': $('input[name="sss_no"]')[0].value,
            'philhealth_no': $('input[name="philhealth_no"]')[0].value,
            'pagibig_no': $('input[name="pagibig_no"]')[0].value,
            'civil_status': $('select[name="civil_status"]')[0].value,
            'gender': $('select[name="gender"]')[0].value,
            'permanent_address_adress': $('input[name="address_1"]')[0].value,
            'permanent_address_city': $('input[name="city_address_1"]')[0].value,
            'permanent_address_zipcode': $('input[name="zipcode_address_1"]')[0].value,
            'permanent_address_contact_no': $('input[name="contact_address_1"]')[0].value,
            'alternative_address_adress': $('input[name="address_2"]')[0].value,
            'alternative_address_city': $('input[name="city_address_2"]')[0].value,
            'alternative_address_zipcode': $('input[name="zipcode_address_2"]')[0].value,
            'alternative_address_contact_no': $('input[name="contact_address_2"]')[0].value,
            'emergency_person_name': $('input[name="emergency_person_name"]')[0].value,
            'emergency_relationship': $('input[name="emergency_relationship"]')[0].value,
            'emergency_address': $('input[name="emergency_address"]')[0].value,
            'emergency_zipcode': $('input[name="emergency_zipcode"]')[0].value,
            'emergency_contactno': $('input[name="emergency_contactno"]')[0].value,
            'image': image_path
        }).then(function (res) {
            openerp.website.form(pathname, 'POST');
            });
});




$(document).on('click', 'a.js_update_application_2', function (ev) {
    ev.preventDefault();
    var pathname = this.pathname;
    openerp.jsonRpc("/jobs/updateApplication2", 'call', {
            'new_applicant_id': $('input[name="new_applicant_id"]')[0].value,
            'denied_visa': $('select[name="denied_visa"]')[0].value,
            'deported': $('select[name="is_deported"]')[0].value
        }).then(function (res) {
            openerp.website.form(pathname, 'POST');
        });
});



